const qualificado7 = Number($json.qualificado7); // pega a string "counter" e converte pra número

const isEven = qualificado7 % 2 === 0; // true se for par
const path = isEven ? 'par' : 'impar';

return [
  {
    json: {
      qualificado7,
      isEven,
      path
    }
  }
];

