if (!$json || !$json["campaign"]) {
  return null; // Retorna nulo se os dados da campanha não existirem
}

const campaign = $json["campaign"]; // Extrai os dados da campanha

// Garante que todos os campos necessários estão definidos
return {
  json: {
    name: campaign["name"] ? campaign["name"].replace('[L081024]', '[L091224]') : "Sem Nome",
    type: campaign["type"] || "single",
    subject: campaign["subject"] || "Sem Assunto",
    html_content: campaign["html_content"] || "Sem Conteúdo",
    from_name: campaign["from_name"] || "Sem Nome Remetente",
    from_email: campaign["from_email"] || "no-reply@example.com",
    reply_to: campaign["reply_to"] || "no-reply@example.com",
    list_ids: campaign["list_ids"] || [],
    status: 0 // Define como rascunho
  }
};
