const negocios = items.map(item => item.json);
const emailNegocios = {};
const duplicatas = [];

// Loop para organizar negócios por e-mail e identificar duplicatas
negocios.forEach(negocio => {
    const emailPessoa = negocio.person_id && negocio.person_id.email ? negocio.person_id.email[0].value : null;

    if (emailPessoa) {
        // Se já existe um negócio com o mesmo e-mail, ele é uma duplicata
        if (emailNegocios[emailPessoa]) {
            duplicatas.push(negocio);  // Adiciona o negócio como duplicata
        } else {
            emailNegocios[emailPessoa] = negocio; // Adiciona o primeiro negócio encontrado para esse e-mail
        }
    }
});

return duplicatas.map(duplicado => ({ json: duplicado }));
