    if (!Array.isArray(dados)) {
      if (dados && typeof dados === 'object') {
        // Se for um objeto Ãºnico, coloca dentro de um array
        dados = [dados];
      } else {
        // Caso contrÃ¡rio, retorna um erro
        return [{ mensagem: "Erro: Os dados fornecidos nÃ£o sÃ£o vÃ¡lidos para processamento." }];
      }
    }

    // FunÃ§Ã£o para validar se a data estÃ¡ nos Ãºltimos 7 dias
    const isDataUltimos7Dias = (dataStr) => {
      if (!dataStr) return false;

      try {
        // Tenta diferentes formatos de data
        let dataMsg;

        // Formato dd/mm/yyyy
        if (dataStr.includes('/')) {
          const [dia, mes, ano] = dataStr.split('/');
          dataMsg = new Date(ano, mes - 1, dia);
        }
        // Formato yyyy-mm-dd
        else if (dataStr.includes('-')) {
          dataMsg = new Date(dataStr);
        }
        // Formato dd/mm/yyyy hh:mm
        else if (dataStr.includes(' ')) {
          const [datePart] = dataStr.split(' ');
          if (datePart.includes('/')) {
            const [dia, mes, ano] = datePart.split('/');
            dataMsg = new Date(ano, mes - 1, dia);
          } else {
            dataMsg = new Date(dataStr);
          }
        }
        // Tenta parseamento direto
        else {
          dataMsg = new Date(dataStr);
        }

        // Verifica se a data Ã© vÃ¡lida
        if (isNaN(dataMsg.getTime())) {
          return false;
        }

        // Calcula a data de 7 dias atrÃ¡s
        const hoje = new Date();
        const seteDiasAtras = new Date();
        seteDiasAtras.setDate(hoje.getDate() - 7);

        // Zera as horas para comparar sÃ³ a data
        hoje.setHours(23, 59, 59, 999);
        seteDiasAtras.setHours(0, 0, 0, 0);
        dataMsg.setHours(0, 0, 0, 0);

        // Verifica se estÃ¡ no range dos Ãºltimos 7 dias
        return dataMsg >= seteDiasAtras && dataMsg <= hoje;

      } catch (error) {
        console.log(`Erro ao processar data: ${dataStr}`, error);
        return false;
      }
    };

    // Filtra apenas mensagens dos Ãºltimos 7 dias
    const dadosFiltrados = dados.filter(item => {
      const dataHorario = item.HorÃ¡rio || item.Data || item.data || item.horario;
      if (!dataHorario) return false;
      return isDataUltimos7Dias(dataHorario);
    });

    // Define datas de hoje e 7 dias atrÃ¡s no formato dd/mm/yyyy
    const hoje = new Date();
    const seteDiasAtras = new Date();
    seteDiasAtras.setDate(hoje.getDate() - 7);

    const formatarData = (date) => {
      const dia = String(date.getDate()).padStart(2, '0');
      const mes = String(date.getMonth() + 1).padStart(2, '0');
      const ano = date.getFullYear();
      return `${dia}/${mes}/${ano}`;
    };

    const periodo = `Data: ${formatarData(seteDiasAtras)} a ${formatarData(hoje)}`;

    if (dadosFiltrados.length === 0) {
      return [{ 
        mensagem: "Nenhuma mensagem encontrada nos Ãºltimos 7 dias.",
        totalMensagens: dados.length,
        mensagensFiltradas: 0,
        periodo
      }];
    }

    const resumo = dadosFiltrados.map(item => {
      const nomeValido = typeof item.Nome === 'string' ? item.Nome.trim() : null;
      const isNomeInvalido = !nomeValido || nomeValido === "?" || nomeValido.toLowerCase() === "desconhecido";
      const nomeUsuario = isNomeInvalido ? "UsuÃ¡rio nÃ£o identificado" : nomeValido;

      const mensagemRespondida = item["Mensagem respondida"]
        ? `Mensagem Respondida: ${item["Mensagem respondida"]}`
        : "Mensagem Respondida: Nenhuma";

      return `Nome: ${nomeUsuario}, Mensagem: ${item.Mensagem || "Mensagem nÃ£o disponÃ­vel"}, ${mensagemRespondida}, HorÃ¡rio: ${item.HorÃ¡rio || item.Data || item.data || item.horario || "HorÃ¡rio nÃ£o informado"}`;
    }).join('\n');

    return [{ 
      mensagem: resumo,
      totalMensagens: dados.length,
      mensagensFiltradas: dadosFiltrados.length,
      periodo
    }];

  } catch (error) {
    return [{ mensagem: `Erro ao processar as mensagens: ${error.message}` }];
  }
})();

