// Captura a resposta da pergunta diretamente
const resposta = $json["Qual dessas opções abaixo faria mais sentido pra você?"];

// Garante que sempre trabalhamos com array, mesmo que só tenha uma resposta
const respostas = Array.isArray(resposta) ? resposta : [resposta];

// Mapeia as respostas com tipo booleano (true ou false)
return respostas.map((resposta, index) => {
  return {
    json: {
      id: index + 1,
      resposta,
      tipo: resposta !== "Outro" // true se for diferente de "Outro", false se for "Outro"
    }
  };
});
